<?phpclass Reports extends MX_Controller {    function construct() {        parent::__construct();        $this->load->library('user_auth');        $this->load->helper('url');        $this->load->library('session');//        $this->load->library('session_view');        $this->load->helper('form');        $this->load->library("pagination");        $this->load->model('attendance_model');    }    function week_starting_day($week_starting_day) {        switch ($week_starting_day) {            case 0 :                $ws_day = "sunday";                break;            case 1 :                $ws_day = "monday";                break;            case 2 :                $ws_day = "tuesday";                break;            case 3 :                $ws_day = "wednesday";                break;            case 4 :                $ws_day = "thursday";                break;            case 5 :                $ws_day = "friday";                break;            case 6 :                $ws_day = "saturday";                break;        }    }    function monthly_reports() {        $this->load->model('attendance_model');        $start_date = date('Y-m-01');        $end_date = date('Y-m-d');        if ($post_data != "") {            if ($post_data['start_date'] != "") {                $start_date = date('Y-m-d', strtotime($post_data['start_date']));            }            if ($post_data['end_date'] != "") {                $end_date = date('Y-m-d', strtotime($post_data['end_date']));            }        }        $data['start_date'] = $start_date;        $data['end_date'] = $end_date;        $this->template->write_view('content', 'attendance/monthly_report_new', $data);        $this->template->render();    }}?>